// vendor bootstrap bundle (tiny helpers)
window._SW = window._SW || {};
_SW.showModal = function(id){ const el=document.getElementById(id); if(el){ el.classList.add('show'); } };
_SW.hideModal = function(id){ const el=document.getElementById(id); if(el){ el.classList.remove('show'); } };
_SW.toast = function(msg, timeout=3000){ const d=document.createElement('div'); d.style.position='fixed'; d.style.right='18px'; d.style.bottom='18px'; d.style.padding='10px 14px'; d.style.background='#071026'; d.style.border='1px solid rgba(255,255,255,0.05)'; d.style.color='#e6eef8'; d.style.borderRadius='10px'; d.style.zIndex=99999; d.textContent=msg; document.body.appendChild(d); setTimeout(()=>d.remove(),timeout); };
